import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import classNames from "classnames/bind";
import style from "./DropdownMenu.module.scss";

let cx = classNames.bind(style);

export default function DropdownMenu({ data }) {
  const [active, setActive] = useState(false);
  const dropdownList = data;
  return (
    <nav className={cx("nav-menu")}>
      <ul className={cx("nav-list")}>
        {dropdownList.map((item, index) => {
          console.log(item.children.lenght);
          return (
            <li
              key={index}
              className={item.children.lenght > 0 && "dropdown"}
              onMouseOver={(e) => {
                setActive(true);
              }}
              onMouseLeave={() => {
                setActive(false);
              }}
            >
              <a href={item.path}>
                {item.content}
                <i>{item.icon && <FontAwesomeIcon icon={item.icon} />}</i>
              </a>
              {item.children && (
                <ul>
                  {item.children.map((itemChild, index) => {
                    return (
                      <li key={index}>
                        <a href={itemChild.path}>{itemChild.content}</a>
                      </li>
                    );
                  })}
                </ul>
              )}
            </li>
          );
        })}
      </ul>
    </nav>
  );
}
