import React from "react";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faFacebookF,
  faTwitter,
  faYoutube,
  faInstagram,
} from "@fortawesome/free-brands-svg-icons";
import {
  faSearch,
  faPhoneAlt,
  faClockFour,
  faCaretDown,
} from "@fortawesome/free-solid-svg-icons";

import logo from "../../../assets/img/logo.svg";
import DropdownMenu from "../../DropdownMenu";
import { Button, Dropdown } from "react-bootstrap";

export default function Header() {
  const socialList = [
    { path: "https://www.facebook.com/rovadex", icon: faFacebookF },
    { path: "https://twitter.com/RovadexStudio", icon: faTwitter },
    { path: "https://www.youtube.com/", icon: faYoutube },
    { path: "https://www.instagram.com/rovadex", icon: faInstagram },
  ];

  const dropdownList = [
    {
      content: "Home",
      path: "#",
      icon: faCaretDown,
      children: [
        {
          path: "",
          content: "Crossfit",
        },
        {
          path: "",
          content: "Fitness",
        },
        {
          path: "",
          content: "Crossfit Lite",
        },
      ],
    },
    {
      content: "About",
      path: "",
      icon: null,
      children: [],
    },
    { content: "Service", path: "", icon: null, children: [] },
    {
      content: "Pages",
      path: "#",
      icon: faCaretDown,
      children: [
        {
          path: "",
          content: "Program",
        },
        {
          path: "",
          content: "Trainer",
        },
      ],
    },
    { content: "News", path: "", icon: null, children: [] },
    { content: "Contacts", path: "", icon: null, children: [] },
  ];
  return (
    <header class="d-flex flex-column header">
      <a href="#" class="nav-btn">
        <span></span>
        <span></span>
        <span></span>
      </a>
      <div class="top-panel">
        <div class="container">
          <div class="header-left">
            <ul class="header-cont">
              <li>
                <i>
                  <FontAwesomeIcon icon={faPhoneAlt} />
                </i>
                <a href="tel:18004886040">1-800-488-6040</a>
              </li>
              <li>
                <i>
                  <FontAwesomeIcon icon={faClockFour} />
                </i>
                Mon - Fri: 8:00AM - 7:00PM | Sat - Sun: Closed
              </li>
            </ul>
          </div>
          <div class="header-right">
            <form class="search-form">
              <input
                type="search"
                class="search-form__field"
                placeholder="Search"
                value=""
                name="s"
              />
              <button type="submit" class="search-form__submit">
                <FontAwesomeIcon icon={faSearch} />
              </button>
            </form>
            <ul class="social-list">
              {socialList.map((item, index) => (
                <li key={index}>
                  <a href={item.path}>
                    <FontAwesomeIcon icon={item.icon} />
                  </a>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
      <div class="header-menu">
        <div class="container">
          <div class="header-logo">
            <a href="index-2.html" class="logo">
              <img src={logo} alt="logo" />
            </a>
          </div>
          <DropdownMenu data={dropdownList} />
        </div>
      </div>
    </header>
  );
}
